import React from "react";
import { servicesData } from '../../services/services'; // updated import
import styles from "./ServicePage.module.scss";
import { FlatList } from '../../navLinksBodys/Servicespage'

export const ServicePage = () => {
    const renderServiceItem = ({ item }) => {
        return (
            <div style={styles.card}>
                <p style={styles.title}>{item.title}</p>
                <p style={styles.description}>{item.description}</p>
                <img src={item.image} alt={item.title} style={styles.image} />
                <p style={styles.offeringsTitle}>Offerings:</p>
                {item.offerings.map((offering, index) => (
                    <p key={index} style={styles.offering}>
                        {`• ${offering}`}
                    </p>
                ))}
            </div>
        );
    };

    return (
        <FlatList
            data={servicesData} // updated to servicesData
            renderItem={renderServiceItem}
            keyExtractor={(item) => item.id}
            contentContainerStyle={styles.container}
        />
    );
};
