import React, { useState } from "react";
import "./ContactUsform.css";
import { ArrangeVerticalCircle } from 'iconsax-react';
import axios from 'axios';

const ContactUsForm = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "", // Updated field
    message: "",
  });

  const [isFormValid, setIsFormValid] = useState(false);
  const [isFormVisible, setIsFormVisible] = useState(true); // Changed to false initially
  const [showMessage, setShowMessage] = useState(false); // New state for the message


  const validateForm = (data) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phoneRegex = /^[0-9]{10}$/; // Basic phone validation for 10 digits

    return (
      data.name.trim() &&
      emailRegex.test(data.email) &&
      phoneRegex.test(data.phone) && // Updated field
      data.message.trim()
    );
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    const updatedData = { ...formData, [name]: value };

    setFormData(updatedData);
    setIsFormValid(validateForm(updatedData));
  };

  const [errorMessage, setErrorMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isFormValid) {
      try {
        await axios.post("http://localhost:8000/contactus/", formData);
        alert("Message sent successfully!");
        resetForm();
      } catch (error) {
        console.error("There was an error sending the message:", error);
        alert("There was an issue submitting the form. Please try again.");
      }
    }
  };

  // In the return statement
{errorMessage && <div className="error">{errorMessage}</div>}

  const resetForm = () => {
    setFormData({
      name: "",
      email: "",
      phone: "", // Updated field
      message: "",
    });
    
    setIsFormValid(false);
  };

  const handleClose = () => {
    resetForm();
    setIsFormVisible(false);
    setShowMessage(true); // Show the message when form is closed
  };

  const toggleFormVisibility = () => {
    setIsFormVisible((prev) => !prev);
    setShowMessage(false); // Hide the message when toggling
  };

  const scrollToBottom = () => {
    window.scrollTo({
      top: document.body.scrollHeight,
      behavior: 'smooth'
    });
  };

  


  return (
    <div
   
    >
      <a href="#" className="back-to-top" style={{ bottom: '30px', position: 'fixed', right: '30px' }} onClick={scrollToBottom}>
        <ArrangeVerticalCircle size="20" color="#3B999F" />
      </a>
      <button onClick={toggleFormVisibility} className="toggleButton" 
     style={{
      position: 'fixed',
      bottom: '20px', // Adjusted to be above the WhatsApp icon
      right: '20px',
      top: '400px',
     
      backgroundColor: '#3B999F', // Adjust the color as needed
      border: 'none',
      borderRadius: '50%',
      width: '50px',
      height: '50px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      cursor: 'pointer',
      boxShadow: '0 2px 10px rgba(0, 0, 0, 0.2)',
    }}
      >
        <span style={{ color: '#3B999F', fontSize: '25px' }}>✉️</span>
      </button>

      {isFormVisible && (
        <div className="formSection shadow">
          <h2 style={{ fontFamily: "Algerian, sans-serif" }}>Write us</h2>

          <form onSubmit={handleSubmit}>
            <div className="formGroup">
              <input
                type="text"
                name="name"
                placeholder="Name"
                value={formData.name}
                onChange={handleChange}
                required
                className="inputFeilds" />
            </div>
            <div className="formGroup">
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                required
                className="inputFeilds" />
            </div>
            <div className="formGroup">
              <input
                type="text"
                name="phone" // Updated field
                placeholder="Phone Number"
                maxLength={10}
                value={formData.phone} // Updated field
                onChange={handleChange}
                required
                className="inputFeilds" />
            </div>
            <div className="formGroup">
              <textarea
                name="message"
                placeholder="Message"
                value={formData.message}
                onChange={handleChange}
                required
                className="inputFeilds" />
            </div>
            <button
              type="submit"
              className={`submitButton ${isFormValid ? "enabled" : "disabled"}`}
              disabled={!isFormValid}
              style={{ cursor: "pointer" }}
            >
              SEND MESSAGE
            </button>
          </form>
          <button
            type="button"
            className="closeButton"
            onClick={handleClose}
            aria-label="Close"
            style={{ background: 'none', border: 'none', cursor: 'pointer', fontSize: '24px' }}
          >
            &times;
          </button>
        </div>
      )}

{showMessage && (
        <div style={{
          position: 'fixed',
          bottom: '80px', // Same as the toggle button's bottom position
          right: '80px', // Adjust to position next to the button
          zIndex: 999,
          backgroundColor: '#fff',
          padding: '10px 15px',
          borderRadius: '5px',
          boxShadow: '0 2px 5px rgba(0, 0, 0, 0.2)',
          display: 'flex',
          alignItems: 'center'
        }}>
          <span style={{ color: '#25D366', fontWeight: 'bold' }}>Chat with us!</span>
        </div>
      )}
    </div>
  );
};

export default ContactUsForm;
