import React, { useEffect,useRef } from 'react';

const Cursor=()=>{
const cursor =useRef()

useEffect(()=>{
    window.addEventListener("mousemove",(e)=>{
const x=e.clientX;
const  y=e.clientY;

cursor.current.style.left =x +"px";
cursor.current.style.top =y +"px";


    })
},[])

    return(
        <div ref={cursor} style={{width:20,height:20,position:'fixed',backgroundColor:'red',borderRadius:20}}></div>
    )
}
export default Cursor;
