import React, { useState } from "react";
import "./ContactUsform.css";

const ContactUsForm = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    message: "",
  });

  const [isFormValid, setIsFormValid] = useState(false);

  const validateForm = (data) => {
    // Basic email and phone regex
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phoneRegex = /^[0-9]{10}$/;

    if (
      data.name.trim() &&
      emailRegex.test(data.email) &&
      phoneRegex.test(data.phone) &&
      data.message.trim()
    ) {
      return true;
    }
    return false;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    const updatedData = { ...formData, [name]: value };

    setFormData(updatedData);
    setIsFormValid(validateForm(updatedData));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isFormValid) {
      console.log(formData);
      // Handle form submission (e.g., send data to your server)
    }
  };

  return (
    <div className="contactContainer">
      <div className="formSection shadow">
        <h2 style={{ fontFamily: "Algerian, sans-serif" }}>Write us</h2>
        <form onSubmit={handleSubmit}>
          <div className="row">
            <div className="col-md-6 formGroup">
              <input
                type="text"
                name="name"
                placeholder="Name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>
            <div className="col-md-6 formGroup">
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
          </div>
          <div className="formGroup">
            <input
              type="text"
              name="phone"
              placeholder="Phone"
              value={formData.phone}
              onChange={handleChange}
              required
            />
          </div>
          <div className="formGroup">
            <textarea
              name="message"
              placeholder="Message"
              value={formData.message}
              onChange={handleChange}
              required
            />
          </div>
          <button
            type="submit"
            className="submitButton"
            disabled={!isFormValid} // Disable until form is valid
          >
            Send Message
          </button>
        </form>
        <button
          type="button"
          className="closeButton"
          data-bs-dismiss="modal"
          aria-label="Close"
        >
          &times;
        </button>
      </div>
    </div>
  );
};

export default ContactUsForm;
