import React, { useState } from "react";
import "./ContactUsform.css"; // Ensure this file exists for styling
import { ArrangeVerticalCircle } from 'iconsax-react';
import axios from 'axios';

const ContactUsForm = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    mobile_number: "",
    message: "",
  });

  const [isFormValid, setIsFormValid] = useState(false);
  const [isFormVisible, setIsFormVisible] = useState(false); // Start with the form hidden
  const [errorMessage, setErrorMessage] = useState("");

  const validateForm = (data) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phoneRegex = /^[0-9]{10}$/; // Basic phone validation for 10 digits

    return (
      data.name.trim() &&
      emailRegex.test(data.email) &&
      phoneRegex.test(data.mobile_number) && // Updated field
      data.message.trim()
    );
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    const updatedData = { ...formData, [name]: value };

    setFormData(updatedData);
    setIsFormValid(validateForm(updatedData));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isFormValid) {
      try {
        await axios.post("http://localhost:8000/contactus/", formData);
        alert("Message sent successfully!");
        resetForm();
      } catch (error) {
        console.error("There was an error sending the message:", error);
        setErrorMessage("There was an issue submitting the form. Please try again.");
      }
    }
  };

  const resetForm = () => {
    setFormData({
      name: "",
      email: "",
      mobile_number: "",
      message: "",
    });
    setIsFormValid(false);
  };

  const toggleFormVisibility = () => {
    setIsFormVisible((prev) => !prev);
    if (isFormVisible) {
      resetForm(); // Reset form when closing
    }
  };

  const scrollToBottom = () => {
    window.scrollTo({
      top: document.body.scrollHeight,
      behavior: 'smooth',
    });
  };

  return (
    <div>
      <a href="#" className="back-to-top" style={{ bottom: '30px', position: 'fixed', right: '30px' }} onClick={scrollToBottom}>
        <ArrangeVerticalCircle size="20" color="#3B999F" />
      </a>
      <button onClick={toggleFormVisibility} className="toggleButton" 
        style={{
          position: 'fixed',
          bottom: '20px',
          right: '20px',
          backgroundColor: '#3B999F',
          border: 'none',
          borderRadius: '50%',
          width: '50px',
          height: '50px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          cursor: 'pointer',
          boxShadow: '0 2px 10px rgba(0, 0, 0, 0.2)',
        }}
      >
        <span style={{ color: '#fff', fontSize: '25px' }}>✉️</span>
      </button>

      {isFormVisible && (
        <div className="formSection shadow">
          <h2 style={{ fontFamily: "Algerian, sans-serif" }}>Write us</h2>
          {errorMessage && <div className="error">{errorMessage}</div>}
          <form onSubmit={handleSubmit}>
            <div className="formGroup">
              <input
                type="text"
                name="name"
                placeholder="Name"
                value={formData.name}
                onChange={handleChange}
                required
                className="inputFields" />
            </div>
            <div className="formGroup">
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                required
                className="inputFields" />
            </div>
            <div className="formGroup">
              <input
                type="text"
                name="mobile_number"
                placeholder="Phone Number"
                maxLength={10}
                value={formData.mobile_number}
                onChange={handleChange}
                required
                className="inputFields" />
            </div>
            <div className="formGroup">
              <textarea
                name="message"
                placeholder="Message"
                value={formData.message}
                onChange={handleChange}
                required
                className="inputFields" />
            </div>
            <button
              type="submit"
              className={`submitButton ${isFormValid ? "enabled" : "disabled"}`}
              disabled={!isFormValid}
              style={{ cursor: "pointer" }}
            >
              SEND MESSAGE
            </button>
          </form>
          <button
            type="button"
            className="closeButton"
            onClick={toggleFormVisibility}
            aria-label="Close"
            style={{ background: 'none', border: 'none', cursor: 'pointer', fontSize: '24px' }}
          >
            &times;
          </button>
        </div>
      )}
    </div>
  );
};

export default ContactUsForm;
