import React from "react";
import { technologies } from "../../services/services";
import "./Blog.css"; // Make sure this CSS file includes your custom styles

// Define a functional component
export default function Blogs() {
  return (
    <div>
      {/* Main header container */}
      <div className="text-center">
        <h1 className="partnership-containers"></h1>
      </div>

      {/* Blog section */}
      <div style={{ paddingTop: "30px", backgroundColor: 'whitesmoke' }}>
        <div className="text-center">
          <u>
            <h2 className="blogHeader">We are Introducing Our Technologies</h2>
          </u>
        </div>

        {/* Cards container */}
        <div className="container mt-5">
          <div className="row">
            {technologies.map((tech, index) => (
              <div
                key={index}
                className={`col-12 col-md-${index % 2 === 0 ? "8" : "4"} mb-4`}
              >
                <div
                  className={`card box-shadow h-100 ${tech.className1}`}
                  style={{
                    backgroundColor: index % 2 === 0 ? "#f9f9f9" : "#e0e0e0",
                    borderRadius: "15px",
                  }}
                >
                  <div className="card-body d-flex">
                    <img
                      src={tech.imageUrl}
                      className="imgstyles img-fluid"
                      alt={tech.title}
                      style={{
                        maxWidth: "150px",
                        height: "auto",
                        borderRadius: "10px",
                      }}
                    />
                    <div className="pl-4" style={{ marginLeft: "40px" }}>
                      <h4
                        style={{
                          fontSize: "28px",
                          fontFamily: "system-ui",
                          fontWeight: "bold",
                        }}
                      >
                        {tech.title}
                      </h4>
                      <span style={{ fontFamily: "system-ui" }}>
                        {tech.description}
                      </span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
