import React from 'react';
import './TeamCards.css'; // Make sure to update the CSS file

const teamMembers = [
  {
    name: 'John Doe',
    designation: 'Software Engineer',
    image: 'https://cdn.pixabay.com/photo/2013/07/21/13/00/rose-165819_640.jpg', // Replace with actual image URLs
    additionalInfo: 'Specializes in frontend development.',
  },
  {
    name: 'Jane Smith',
    designation: 'UI/UX Designer',
    image: 'https://cdn.pixabay.com/photo/2014/04/03/10/32/user-312602_640.png',
    additionalInfo: 'Expert in user experience design.',
  },
  // Add more team members as needed
];

const TeamCards = () => {
  return (
    <div className="team-container" style={{ backgroundColor: 'whitesmoke', marginTop: 30, paddingTop: 20 }}>
      <h1 style={{ color: '#41999c', textAlign: 'center' }}>Meet our creative minds</h1>
      <div className="row">
        {teamMembers.map((member, index) => (
          <div key={index} className="col-12 col-sm-6 col-md-4 col-lg-3 mb-4">
            <div className="team-card hover-effect">
              <img
                src={member.image}
                alt={`Profile of ${member.name}`}
                className="team-img-top"
              />
              <div className="team-body">
                <h5 className="team-title">{member.name}</h5>
                <p className="team-text">{member.designation}</p>
                <p className="team-additional-info">{member.additionalInfo}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TeamCards;
