import React, { useState } from "react";
import axios from "axios";
import "./Careersform.css";

const CareersForm = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone_number: "",
    resume: null,
  });

  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData({ 
      ...formData, 
      [name]: name === "resume" ? files[0] : value 
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { name, email, phone_number, resume } = formData;

    if (!name || !email || !phone_number || !resume) {
      setErrorMessage("Please fill in all fields.");
      return;
    }

    const formDataToSend = new FormData();
    formDataToSend.append("name", name);
    formDataToSend.append("email", email);
    formDataToSend.append("phone_number", phone_number);
    formDataToSend.append("resume", resume);

    try {
      await axios.post("http://localhost:8000/applications/", formDataToSend, {
        headers: {
          "Content-Type": "multipart/form-data"
        }
      });
      setSuccessMessage("Application submitted successfully!");
      setErrorMessage("");
      resetForm();
    } catch (error) {
      console.error("Error submitting application:", error);
      setErrorMessage("There was an issue submitting your application. Please try again.");
    }
  };

  const resetForm = () => {
    setFormData({ name: "", email: "", phone_number: "", resume: "" });
  };

  return (
    <div className="contactContainer1">
      <div className="formSection1">
        <h2>Apply for a Position</h2>
        {errorMessage && <div className="error">{errorMessage}</div>}
        {successMessage && <div className="success">{successMessage}</div>}
        <form onSubmit={handleSubmit}>
          <div className="formGroup1">
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={formData.name}
              onChange={handleChange}
              required
            />
          </div>
          <div className="formGroup1">
            <input
              type="email"
              name="email"
              placeholder="Enter Email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="formGroup1">
            <input
              type="text"
              name="phone_number"
              placeholder="Enter Phone Number"
              value={formData.phone_number}
              onChange={handleChange}
              required
              maxLength={10}
            />
          </div>
          <div className="formGroup1">
            <input
              name="resume"
              type="file"
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit" className="submitButton1">
            Submit Application
          </button>
        </form>
      </div>
    </div>
  );
};

export default CareersForm;
