import React,{useState,useEffect} from "react";
import Testimonials from "./testimonilas/Testimonials";
import ServiceCards from "./services/ServiceCards";
import Banner from "./Banner";
import Stories from "./testimonilas/Stories";
import HappyClients from "./testimonilas/HappyClintsCountingWidget";
import WhatsAppButton from "../whatsapp/WhatsAppButton";
import ContactUsForm from "../forms/ContactUsForm";
import "./section.css";


export default function Body() {


  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    // Show cards with delay
    const showCards = () => {
      setTimeout(() => {
        document.getElementById("card1").style.opacity = "1";
        document.getElementById("card1").style.transform = "translateY(0)";
      }, 500);

      setTimeout(() => {
        document.getElementById("card2").style.opacity = "1";
        document.getElementById("card2").style.transform = "translateY(0)";
      }, 1000);

      setTimeout(() => {
        document.getElementById("card3").style.opacity = "1";
        document.getElementById("card3").style.transform = "translateY(0)";
      }, 1500);
    };

    showCards();

    // Show modal after 5 minutes (300,000 ms)
    const showModalTimeout = setTimeout(() => {
      setShowModal(true);
    }, 3000); // 5 minutes

    return () => clearTimeout(showModalTimeout); // Cleanup the timeout on component unmount
  }, []);

  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <main>
      <Banner />
      <ServiceCards />
      <Testimonials />
      {/* <HappyClients/> */}
      <WhatsAppButton/>
      <Stories />


      {showModal && (
  <div
    className="modal show d-block"
    id="contactModal"
    tabIndex="-1"
    role="dialog"
    aria-labelledby="contactModalLabel"
    aria-hidden="true"
  >
    <div
      className="modal-dialog"
      role="document"
      style={{ 
        marginTop: "6%", 
        position: "fixed", 
        right: "0", 
        top: "16%", 
        transform: "translateX(0)" 
      }}
    >
      <div className="modal-content border-0 bg-transparent">
        <ContactUsForm closeModal={closeModal} />
      </div>
    </div>
  </div>
)}
    </main>
  );
}
