import React, { useState } from "react";
import { technologies } from "../../services/services";
import "./Blog.css";


export default function Blogs() {
  const [showCards, setShowCards] = useState([]);

  const handleRevealCards = () => {
    setShowCards(true);
  };
  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setShowCards(new Array(technologies.length).fill(true)); // Show all cards after a delay
    }, 300); // Delay before showing cards
    return () => clearTimeout(timeoutId);
  }, []);


  return (
    <div>
      <div>
        <h1 className="partnership-containers"></h1>
      </div>

      <div className="container mt-5">
        {/* Header Section */}
        <div className="row">
          <div className="col-md-1"></div>
          <div className="col-md-10">
            <h2 className="text-center" style={{ color: "#333", fontWeight: "bold" }}>
              We are Introducing Our Technologies
            </h2>
          </div>
        </div>

        {/* Initial "Click Here" Card */}
        {!showCards && (
          <div className="row mt-4">
            <div className="col-md-3"></div>
            <div className="col-md-6">
              <div className="card text-center p-4" style={{ boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)" }}>
                <h4 style={{ color: '#B87333', fontFamily: 'system-ui' }}>Click Here</h4>
                <p style={{ color: 'black', fontFamily: 'system-ui' }}>Explore the diverse technologies we utilize to create innovative and impactful solutions.</p>
                <button
                  className="btn btn-primary"
                  style={{ backgroundColor: "#B87333", border: "none" }}
                  onClick={handleRevealCards}
                >
                  Click to Reveal Cards
                </button>
              </div>
            </div>
            <div className="col-md-3"></div>
          </div>
        )}

        {/* All Cards will be shown once the button is clicked */}
        {showCards && (
          <div className="row mt-4">
          {/* Map through technologies array to render each card */}
          {technologies.map((tech, index) => (
            <div
              className={`col-md-6 mb-4 card ${
                showCards[index] ? (index % 2 === 0 ? "show" : "card-left show") : ""
              }`}
              key={index}
            >
              <div
                className="card h-100 text-center"
                style={{ boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)" }}
              >
                <div className="card-body">
                  {/* Image Section */}
                  <img
                    src={tech.imageUrl}
                    alt={tech.title}
                    className="img-fluid"
                    style={{
                      width: "100%",
                      height: "200px",
                      objectFit: "cover",
                      marginBottom: "20px",
                    }}
                  />
                  {/* Title and Description */}
                  <h5 style={{ fontSize: "24px", fontFamily: "system-ui", color: '#B87333', textDecoration: 'underline' }}>
                    {tech.title}
                  </h5>
                  <p style={{ fontFamily: "system-ui", color: "#666", marginTop: "10px", color: 'black' }}>
                    {tech.description}
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
        )}
      </div>
    </div>
  );
}
