import React from "react";
import { technologies } from "../../services/services";
import "./Blog.css"; // Ensure your custom styles are in this CSS file

// Define a functional component
export default function Blogs() {
  return (
    <div>
      <div className="container" style={{ paddingTop: "30px", backgroundColor: 'whitesmoke' }}>
        <div className="row">
          {/* Left column (col-1) */}
          <div className="col-12 col-md-4">
            <div className="sticky-top" style={{ top: "20px" }}>
              <u>
                <h2 className="blogHeader">We are Introducing Our Technologies</h2>
              </u>
              <p style={{ fontSize: "18px", fontFamily: "system-ui", marginTop: "20px" }}>
                Explore the diverse technologies we utilize to create innovative and impactful solutions.
              </p>
            </div>
          </div>

          {/* Right column (col-2) */}
          <div className="col-12 col-md-8">
            <div className="row">
              {technologies.map((tech, index) => (
                <div key={index} className={`col-12 col-md-${index % 2 === 0 ? "6" : "6"} mb-4`}>
                  <div
                    className={`card box-shadow h-100 ${index % 2 === 0 ? "taller-card" : "smaller-card"}`}
                    style={{
                      borderRadius: "15px",
                    }}
                  >
                    <div className="card-body d-flex">
                      <img
                        src={tech.imageUrl}
                        className="imgstyles img-fluid"
                        alt={tech.title}
                        style={{
                          maxWidth: "150px",
                          height: "auto",
                          borderRadius: "10px",
                        }}
                      />
                      <div className="pl-4" style={{ marginLeft: "40px" }}>
                        <h4
                          style={{
                            fontSize: "28px",
                            fontFamily: "system-ui",
                            fontWeight: "bold",
                          }}
                        >
                          {tech.title}
                        </h4>
                        <span style={{ fontFamily: "system-ui" }}>
                          {tech.description}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
