import React, { useState } from "react";
import { technologies } from "../../services/services";
import "./Blog.css"; // Ensure your necessary styles are included

export default function CardWithReveal() {
  const [showCards, setShowCards] = useState(false); // State to toggle card visibility

  // Function to reveal all cards
  const handleRevealCards = () => {
    setShowCards(true);
  };

  return (
    <div className="container mt-5">
      {/* Header Section */}
      <div className="row">
        <div className="col-md-1"></div>
        <div className="col-md-10">
          <h2 className="text-center" style={{ color: "#333", fontWeight: "bold" }}>
            We are Introducing Our Technologies
          </h2>
        </div>
      </div>

      {/* Initial "Click Here" Card */}
      {!showCards && (
        <div className="row mt-4">
          <div className="col-md-3"></div>
          <div className="col-md-6">
            <div className="card text-center p-4" style={{ boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)" }}>
              <h4>Click Here</h4>
              <p>Click the button below to reveal all technologies.</p>
              <button
                className="btn btn-primary"
                style={{ backgroundColor: "#B87333", border: "none" }}
                onClick={handleRevealCards}
              >
                Click to Reveal Cards
              </button>
            </div>
          </div>
          <div className="col-md-3"></div>
        </div>
      )}

      {/* All Cards will be shown once the button is clicked */}
      {showCards && (
        <div className="row mt-4">
          {/* Map through technologies array to render each card */}
          {technologies.map((tech, index) => (
            <div className="col-md-6 mb-4" key={index}>
              <div className="card h-100" style={{ boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)" }}>
                <div className="card-body d-flex align-items-center">
                  <img
                    src={tech.imageUrl}
                    alt={tech.title}
                    className="img-fluid mr-3"
                    style={{ width: "80px", height: "80px", borderRadius: "50%" }}
                  />
                  <div>
                    <h5 style={{ fontSize: "24px", fontFamily: "system-ui" }}>{tech.title}</h5>
                    <p style={{ fontFamily: "system-ui", color: "#666" }}>{tech.description}</p>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
