import React from "react";
import { globalServiceData } from "../../services/services";
import "./aboutUs.css";
import HappyClients from "../../testimonilas/HappyClintsCountingWidget";
import { technologies } from "../../services/services";

export default function AboutUsScreen() {
  const text = "Our Partnerships";
  return (
    <div>
      <div className="partnership-container">
      </div>

<div style={{marginTop:20}}>
  <h5 style={{textAlign:'center',marginLeft:'15%',width:'70%',fontFamily:'system-ui',fontSize:40}}>Trishoka Digital Services crafts digital marketing strategies to elevate your brand's online presence.</h5>
  <div style={{textAlign:'center',marginLeft:'20%',width:'55%',fontFamily:'system-ui',marginTop:20}}>
  <span>"Trishoka Digital Services crafts effective digital marketing strategies to elevate your brand online. Our team blends creativity, strategic insight, and advanced technology. We aim to deliver results that help your business stand out."</span>
  </div>
</div>
<HappyClients/>

<div>
      <div className="container" style={{ paddingTop: "30px", backgroundColor: 'whitesmoke' }}>
        <div className="row">
          {/* Left column (col-1) */}
          <div className="col-12 col-md-4">
            <div className="sticky-top" style={{ top: "20px" }}>
              <u>
                <h2 className="blogHeader" style={{ color: '#B87333' }}>Our values drives our decisions and guide our actions.</h2>
              </u>
            </div>

          </div>

          {/* Right column (col-2) */}
          <div className="col-12 col-md-8">
            <div>
              {technologies.map((tech, index) => (
                <div key={index} className={`col-12 col-md-${index % 2 === 0 ? "6" : "6"} mb-4`}>
                  <div
                    className={`card box-shadow h-100 ${index % 2 === 0 ? "taller-card" : "smaller-card"}`}
                    style={{
                      borderRadius: "15px",
                    }}
                  >
                    {/* Image on top */}
                    <img
                      src={tech.imageUrl}
                      className="imgstyles img-fluid"
                      alt={tech.title}
                      style={{
                        width: "100%",
                        height: index % 2 === 0 ? "200px" : "150px",
                        objectFit: "cover",
                        borderRadius: "15px 15px 0 0",
                      }}
                    />

                    {/* Content below the image */}
                    <div className="card-body" style={{ textAlign: "center" }}>
                      <h4
                        style={{
                          fontSize: "24px",
                          fontFamily: "system-ui",
                          fontWeight: "bold",
                          color: '#B87333'
                        }}
                      >
                        {tech.title}
                      </h4>
                      <p style={{ fontFamily: "system-ui", color: 'black' }}>
                        {tech.description}
                      </p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>

    </div>
  );
}
