import React from "react";
import { globalServiceData } from "../../services/services";
import "./aboutUs.css";
import HappyClients from "../../testimonilas/HappyClintsCountingWidget";
import { technologies1 } from "../../services/services";

export default function AboutUsScreen() {
  const text = "Our Partnerships";
  return (
    <div>
      <div className="partnership-container">
      </div>

      <div style={{ marginTop: 20 }}>
        <h5 style={{ textAlign: 'center', marginLeft: '15%', width: '70%', fontFamily: 'system-ui', fontSize: 40 }}>Trishoka Digital Services crafts digital marketing strategies to elevate your brand's online presence.</h5>
        <div style={{ textAlign: 'center', marginLeft: '20%', width: '55%', fontFamily: 'system-ui', marginTop: 20 }}>
          <span>"Trishoka Digital Services crafts effective digital marketing strategies to elevate your brand online. Our team blends creativity, strategic insight, and advanced technology. We aim to deliver results that help your business stand out."</span>
        </div>
      </div>
      <div>
        <div className="container" style={{ paddingTop: "30px", backgroundColor: 'whitesmoke', marginTop: 30 }}>
          <div className="row">
          <div>
          <h3>TRANSFORM YOUR BUSINESS WITH INFIONIC</h3>
          <span> Empower your organization with Infionicâ€™s Expertise</span>
          </div>
            {/* Right column (3 by 3 cards) */}
            {technologies1.map((tech, index) => (
              <div key={index} className="col-12 col-md-4 mb-4">
                <div
                  className="card box-shadow smaller-card hover-card"
                  style={{
                    borderRadius: "15px",
                    height: '100%',
                    transition: 'background-color 0.2s, color 0.2s',
                  }}
                >
                  <div className="card-body" style={{ textAlign: "left", position: "relative", zIndex: 1 }}>
                    <div className="count-icon-container" style={{ display: 'flex', alignItems: 'center' }}>
                      <h2 style={{ marginRight: '10px', color: 'black' }}>{tech.count}</h2>
                      <img src={tech.iconUrl} alt="icon" className="tech-icon" />
                    </div>
                    <h4
                      style={{
                        fontSize: "24px",
                        fontFamily: "system-ui",
                        fontWeight: "bold",
                        color: 'black',
                        marginBottom: '10px',
                      }}
                    >
                      {tech.title}
                    </h4>
                    <p className="tech-description" style={{ color: 'black' }}>
                      {tech.description}
                    </p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      <HappyClients />
    </div>
  );
}
