import React from "react";
import styles from "./ServicePage.css";

export const ServicePageCard = ({ title, description, image, offerings }) => {
    return (
        <div className={styles.card}> {/* Use className instead of style */}
            <h1 className={styles.title}>{title}</h1> {/* Fix to use title prop */}
            <p className={styles.description}>{description}</p> {/* Fix to use description prop */}
            <img src={image} alt={title} className={styles.image} /> {/* Use img instead of image */}
            <h1 className={styles.offeringsTitle}>Offerings:</h1>
            {offerings.map((offering, index) => ( // Fix to use offerings prop
                <p key={index} className={styles.offering}>
                    {`• ${offering}`}
                </p>
            ))}
        </div>
    );
};
