import React, { useState, useEffect } from "react";
import "./Careersform.css";

const CareersForm = () => {
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        phoneNumber: "",
        uploadResume: "",
    });
    
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isFormValid, setIsFormValid] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(formData);
        // Handle form submission (e.g., send data to your server)

        // Close modal after submitting
        setIsModalOpen(false);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    // Form validation function to check if all fields are filled
    useEffect(() => {
        const isValid = formData.name && formData.email && formData.phoneNumber && formData.uploadResume;
        setIsFormValid(isValid);
    }, [formData]);

    return (
        <div className="contactContainer1">
            <div className="formSection1">
                <h2>Reach Out Our Team</h2>
                <form onSubmit={handleSubmit}>
                    <div className="formGroup1">
                        <input
                            type="text"
                            name="name"
                            placeholder="Name"
                            value={formData.name}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="formGroup1">
                        <input
                            type="email"
                            name="email"
                            placeholder="Enter Email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />
                    </div>
                    <div className="formGroup1">
                        <input
                            type="text"
                            name="phoneNumber"
                            placeholder="Enter Phone Number"
                            value={formData.phoneNumber}
                            onChange={handleChange}
                            maxLength={10}
                            required
                        />
                    </div>
                    <div className="formGroup1">
                        <input
                            name="uploadResume"
                            type="file"
                            onChange={handleChange}
                            required
                        />
                    </div>

                    {/* Send Message button that submits form and closes modal */}
                    <button
                        type="submit"
                        className="submitButton1"
                        disabled={!isFormValid}
                        aria-modal={isModalOpen}
                    >
                        Send Message
                    </button>
                </form>
            </div>

            {isModalOpen && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <button className="close-modal" onClick={closeModal}>
                            &times;
                        </button>
                        <h2>Form Submitted Successfully!</h2>
                        <p>We have received your information.</p>
                    </div>
                </div>
            )}
        </div>
    );
};

export default CareersForm;
