import React, { useState, useRef,useEffect } from "react";
import "./Header.css";
import logo from "../../assets/images/trishokalogonobg.png";
import { Link } from "react-router-dom";
import Cursor from "../../Cursor/Cursor";
export default function Header(props) {
  
  const cursor =useRef()

  useEffect(()=>{
      window.addEventListener("mousemove",(e)=>{
  const x=e.clientX;
  const  y=e.clientY;
  
  cursor.current.style.left =x +"px";
  cursor.current.style.top =y +"px";
  
  
      })
  },[])
  

  const navLinks = ["Services","Partnership", "Blogs", 
    "Careers", "ContactUs"];
  return (
    <div>
      <nav className="navbar navbar-expand-lg navbar-light "ref={cursor} style={{width:20,height:20,position:'fixed',backgroundColor:'red',borderRadius:20,pointerEvents:'none'}}>
        <div className="container-fluid" style={{ marginLeft:'4%'}}>
          <a className="navbar-brand" href="navbar.html">
            <img src={logo} width="70px" alt="Trishoka Logo" />
          </a>
          <div class="text-center navbar-text">
            <h1 class="mb-2" style={{fontFamily:'system-ui'}}>TRISHOKA</h1>
            <p class="mb-0" style={{fontFamily:'system-ui'}}>Digital Services</p>
          </div>

          <button
            className="navbar-toggler"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarTogglerDemo02"
            aria-controls="navbarTogglerDemo02"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div
            className="collapse navbar-collapse"
            id="navbarTogglerDemo02"
            style={{ marginLeft: "10%" }}
          >
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <Link
                  className="nav-link active"
                  aria-current="page"
                  to="/"
                  style={{ color: "whitesmoke",fontFamily:'system-ui' }}
                >
                  Home
                </Link>
              </li>
              {navLinks.map((navlink, index) => {
                return (
                  <li className="nav-item" key={index}>
                    <Link className="nav-link" to={`/${navlink}`} style={{fontFamily:'system-ui'}}>
                      {navlink}
                    </Link>
                  </li>
                );
              })}
            </ul>
          </div>
        </div>
      </nav>
    </div>
  );
}
