import React from 'react';
import './TeamCards.css'; // Import the CSS file for styles

const TeamCards = () => {
  return (
    <div className="container" style={{ backgroundColor: 'whitesmoke', marginTop: 30, paddingTop: 20 }}>
      <h1 style={{ color: '#41999c', textAlign: 'center' }}>Meet our creative minds</h1>
      <div className="row">
        {Array.from({ length: 8 }, (_, index) => (
          <div key={index} className="col-12 col-sm-6 col-md-4 col-lg-3 mb-4">
            <div className="card hover-card">
              <img
                src={`https://via.placeholder.com/150`} // Replace with actual image URLs
                alt="Profile"
                className="card-img-top" 
              />
              <div className="card-body">
                <h5 className="card-title">Name {index + 1}</h5>
                <p className="card-text">Designation {index + 1}</p>
                <p className="additional-info">Additional info about this team member.</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TeamCards;
