import React from 'react';

const TeamCards = () => {
  return (
    <div className="container" style={{ paddingTop: "30px", backgroundColor: 'whitesmoke' }}>
      <div className="row">
        <h1>Meet our creative minds</h1>
        {Array.from({ length: 8 }, (_, index) => (
          <div key={index} className="col-12 col-sm-6 col-md-4 col-lg-3 mb-4">
            <div className="card hover-card" style={{ borderRadius: "15px", textAlign: "center", backgroundColor: "#f8f9fa", transition: 'transform 0.3s' }}>
              <img
                src={`https://via.placeholder.com/150`} // Replace with actual image URLs
                alt="Profile"
                className="card-img-top" 
                style={{ borderRadius: "15px 15px 0 0" }} 
              />
              <div className="card-body">
                <h5 className="card-title">Name {index + 1}</h5>
                <p className="card-text">Designation {index + 1}</p>
                <p className="additional-info" style={{ display: 'none', transition: 'opacity 0.5s ease' }}>
                  Additional info about this team member.
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default TeamCards;
