import React from "react";
import styles from './ServicePage.module.scss'; // Import your SCSS module

const ServiceCard = ({ title, subtitle, description, offerings, imgSrc, imgAlt, color }) => (
  <div className={`${styles.servicecard} ${styles.serviceIndividual}`}>
    <div className={styles.grid}>
      <div className={`${styles.gCol} ${styles.gCol6}`}>
        <h1 className={styles.serviceTitle} style={{ color }}>{title}</h1>
        <h4 className={styles.serviceSubtitle}>{subtitle}</h4>
        <p className={styles.serviceDescription}>{description}</p>
        <h2 className={styles.offeringsTitle} style={{ color }}>Offerings:</h2>
        <ul className={styles.offeringsList}>
          {offerings.map((item, index) => (
            <li key={index} className={styles.offeringItem}>{item}</li>
          ))}
        </ul>
      </div>
      <div className={styles.gCol6}>
        <img src={imgSrc} alt={imgAlt} className={styles.serviceImage} />
      </div>
    </div>
  </div>
);

export const Servicepage = () => {
  const services = [
    {
      title: "SEO BOOSTER",
      subtitle: "Perfect Your Website for Web Visibility",
      description: "We at TRISHOKA Digital Services know that Search Engine Optimization is key for online success...",
      offerings: [
        "On-Page Optimization",
        "Technical SEO",
        "Content Strategy",
        "Link Building",
        "Image Optimization",
      ],
      imgSrc: "images/seo1.png",
      imgAlt: "SEO booster service",
      color: "#B87333",
    },
    {
      title: "BUSINESS GROWTH",
      subtitle: "Strategic Objectives and Audience Understanding",
      description: "At TRISHOKA Digital Services, we are committed to fostering strategic business growth that drives companies to new heights.",
      offerings: [
        "Market Expansion",
        "Operational Efficiency",
        "Customer Satisfaction",
      ],
      imgSrc: "images/growthimage.jpeg",
      imgAlt: "Business growth service",
      color: "#da9f68",
    },
    // Add other services in the same format...
  ];

  return (
    <div className={styles.contentWrapper}>
      <header className={styles.serviceHeading}>
        <h1 className={styles.serviceTitle} style={{ color: "#B87333", textAlign: "center" }}>
          Welcome To Our Services
        </h1>
        <h6 className={styles.subtitle}>Discover Tailored Solutions to Elevate Your Success</h6>
        <p className={styles.description}>
          You can Explore the best Digital & Marketing Portfolio Services with TRISHOKA
        </p>
      </header>
      <section id="services" className={styles.serviceCards}>
        {services.map((service, index) => (
          <ServiceCard key={index} {...service} />
        ))}
      </section>
    </div>
  );
};
