import React, { useState } from "react";
import "./ContactUsform.css";
import { ArrangeVerticalCircle } from 'iconsax-react';
import axios from 'axios';

const ContactUsForm = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    phone: "",
    message: "",
  });

  const [isFormValid, setIsFormValid] = useState(false);
  const [isFormVisible, setIsFormVisible] = useState(true);

  const validateForm = (data) => {
    // Basic email and phone regex
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const phoneRegex = /^[0-9]{10}$/;

    return (
      data.name.trim() &&
      emailRegex.test(data.email) &&
      phoneRegex.test(data.mobile_number) &&
      data.message.trim()
    );
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    const updatedData = { ...formData, [name]: value };

    setFormData(updatedData);
    setIsFormValid(validateForm(updatedData));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isFormValid) {
      console.log(formData);
      alert("Message sent successfully!");
      resetForm(); // Close the form after successful submission
    }
  };

  const resetForm = () => {
    setFormData({
      name: "",
      email: "",
      phone: "",
      message: "",
    });
    setIsFormVisible(false); // Hide the form
    setIsFormValid(false);
  };

  const handleClose = () => {
    resetForm();
  };

  if (!isFormVisible) return null; // Hide form if not visible

  const scrollToBottom = () => {
    window.scrollTo({
      top: document.body.scrollHeight, // Scroll to the bottom of the page
      behavior: 'smooth' // Smooth scrolling effect
    });
  };

  return (
    <div className="formSection shadow">
 
    <a href="#" class=" back-to-top" style={{top:0,position:'fixed',left:5,}}  onClick={scrollToBottom} >
      {/* <ArrowUp size="20" color="#3B999F"/> */}
      {/* <ArrowCircleUp size="20" color="#3B999F"/> */}
      <ArrangeVerticalCircle size="20" color="#3B999F"/>
      </a>
    <h2 style={{ fontFamily: "Algerian, sans-serif" }}>Write us</h2>


    <form onSubmit={handleSubmit}>
      
      <div className="formGroup">
      {/* <a href="#" class="btn btn-lg btn-primary btn-lg-square back-to-bottom"><i class="bi bi-arrow-down"></i></a> */}
        <input
          type="text"
          name="name"
          placeholder="Name"
          value={formData.name}
          onChange={handleChange}
          required
          className="inputFeilds"
        />
      </div>
      <div className="formGroup">
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
          className="inputFeilds"
        />
      </div>
      <div className="formGroup">
        <input
          type="text"
          name="phone"
          placeholder="Phone"
          maxLength={10}
          value={formData.phone}
          onChange={handleChange}
          required
          className="inputFeilds"
        />
      </div>
      <div className="formGroup">
        <textarea
          name="message"
          placeholder="Message"
          value={formData.message}
          onChange={handleChange}
          required
          className="inputFeilds"
        />
      </div>
      <button
        type="submit"
        className={`submitButton ${isFormValid ? "enabled" : "disabled"}`}
        disabled={!isFormValid}
        style={{ cursor: "pointer" }}
      >
        SEND MESSAGE
      </button>
    </form>
    <button
      type="button"
      className="closeButton"
      onClick={handleClose}
      aria-label="Close"
    >
      &times;
    </button>
  </div>
  
  );
};

export default ContactUsForm;
