import React, { useEffect, useState } from "react";
import styles from "./Careers.module.scss";
import "bootstrap/dist/css/bootstrap.min.css";
import { jobs, reviews } from "../../services/services"
import CareersForm from "../../../careersforms/Careersform";

 export const Careers = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsVisible(true); // Set visibility after 1 second
    }, 1000);
    return () => clearTimeout(timer); // Cleanup timer on unmount
  }, []);
  return (
    <div>
      <header className={styles.header}>
        <marquee className={styles.marquee}>
          Come let's build Something of great Value
        </marquee>
      </header>

    
<div className={styles.CareeresBg}>
<section className="text-center">
        <h2 className={styles.sectionTitle}>CAREERS</h2>
      </section>

<div className="container">
        {/* Digital Marketing Card */}
        {jobs.map((job, index) => {
          return (
            <div>
              <div
                className="d-flex justify-content-center align-items-center"
                style={{ minHeight: "60vh" }}
              >
                <div
                  className={`card p-3 ${styles.animatedCard}  ${
                    isVisible ? styles.visible : ""
                  }`}
                  style={{ width: "100%", maxWidth: "800px" }}
                >
                  <h4
                    className="mb-2"
                    style={{ fontFamily:'system-ui' }}
                  >
                    {job.title}
                  </h4>
                  <div
                    className="d-flex flex-wrap mb-2"
                    style={{ gap: "20px" }}
                  >
                    <p className={styles.tag}>{job.experience}</p>
                    <p className={styles.tag}>{job.availability}</p>
                    <p className={styles.tag}>{job.location}</p>
                  </div>
                  <hr />
                  <h4 className="mb-3" style={{fontFamily:'system-ui'}}>Minimum Qualification</h4>
                  <ul style={{fontFamily:'system-ui'}}>
                    {job.qualifications.map((qual, index) => (
                      <li key={index}>{qual}</li>
                    ))}
                  </ul>
                  <div className="d-flex justify-content-between">
                    <button className="btn btn-outline-primary btn-sm" style={{fontFamily:'system-ui'}}>
                      Show More
                    </button>
                    <button
                      className={`btn btn-sm ${styles.applyButton}`} style={{fontFamily:'system-ui'}}
                      data-bs-toggle="modal"
                      data-bs-target="#applyModal"
                    >
                      Apply Now
                    </button>
                  </div>
                </div>
              </div>
            </div>
          );
        })}
      </div>

      <div className="modal-dialog" role="document" style={{marginTop:'6%'}}>
          <div className="modal-content">
            <CareersForm/>
          </div>
        </div>

      {/* Reviews */}
      <section className="container text-center my-4">
        <h2 className={styles.reviewsTitle}>
          What People are Saying About Us!
        </h2>
        <div className="row justify-content-center">
          {reviews.map((review, index) => {
            return (
              <div className="col-12 col-md-6 mb-4 mt-4">
                <div className={`${styles.reviewCard} p-3 border rounded`}>
                  <h5 className={styles.reviewAuthor}>{review.author}</h5>
                  <p className={styles.reviewText}>{review.text}</p>
                  <div className={styles.stars}>
                    {[...Array(review.stars)].map((_, i) => (
                      <i key={i} className="fas fa-star checked"></i>
                    ))}
                    {[...Array(5 - review.stars)].map((_, i) => (
                      <i key={i} className="far fa-star"></i>
                    ))}
                  </div>
                  <span className="text-muted">{`Posted on ${review.postedOn}`}</span>
                </div>
              </div>
            );
          })}
        </div>
      </section>
</div>
      
    </div>
  );
};

export default Careers;
